INSERT INTO crop_variety(crop_id, name_ro, name_ru, description_ro)
VALUES (1, 'AKTEUR', 'AKTEUR', 'Cel mai cunoscut soi de grau din generatia noua, Akteur si-a capatat notorietatea in randul cultivatorilor datorita rezultatelor foarte bune, in special din Banat, Crisana si centrul tarii. Este un soi nearistat, care performeaza pe toate tipurile de sol (inclusiv pe cele din zonele secetoase) si este recunoscut pentru toleranta crescuta fata de boli si calitatea recoltei ELITA (excelent pentru panificatie). Este cel mai tardiv soi din portofoliu, fiind pretabil pentru semanatul in afara epocii datorita dezvoltarii puternice in toamna. Nu se recomanda semanatul timpuriu deoarece poate conduce la slabirea tolerantei la ger. Poate asigura un nivel ridicat de productivitate in conditii tehnologice foarte variate; datorita calitatii superioare a recoltei recomandam aplicarea unei tehnologii intensive. Potentialul ridicat de productie trebuie sustinut cu un nivel ridicat de fertilizare si tratament cu fungicid. Foliajul este abundet, lat si lax cu continut ridicat de pruina pana spre finalul perioadei de vegetatie. In plus, are un sistem radicular bine dezvoltat, iar paiul este viguros si foarte tolerant la inclinare si frangere.');

INSERT INTO crop_variety(crop_id, name_ro, name_ru, description_ro)
